import React from "react"
const Context = React.createContext()

function ContextProvider(props) {

    const sliderInfo = {
        zIndex: 1,
        slidesToShow: 1,
        slides: [
          {
            image:
              "https://media.istockphoto.com/photos/experienced-engineer-explaining-the-problems-in-construction-works-picture-id1267010934?k=20&m=1267010934&s=612x612&w=0&h=orOoQ1keF4d0axah0pr5HENABscij3Nxg-PKUV_7YK0=",
            text: "Sécurité",
          },
          {
            image:
              "https://media.istockphoto.com/photos/multi-ethic-workers-talking-at-construction-site-reviewing-plans-picture-id1179153745?k=20&m=1179153745&s=612x612&w=0&h=q0uTLFFQvR2DfZk1Fn3ZTHmm1JhnqV7oa6Y6UdTThCo=",
            text: "Sûreté",
          },
          {
            image:
              "https://media.istockphoto.com/photos/construction-workers-working-on-construction-site-picture-id170961867?k=20&m=170961867&s=612x612&w=0&h=-rWdIQi42-JRsoUDxHJpXJf8Wc5mgQMZmNfN_v0Yuco=",
            text: "Sérénité",
          },
          {
            image:
              "https://media.istockphoto.com/photos/takes-a-risk-at-work-picture-id1132245221?k=20&m=1132245221&s=612x612&w=0&h=QYTwsDYX7bpe72Kvx50AzpXLdv9b4BVLp1IQX4hp9S8=",
            text: "Professionalisme",
          },
          {
            image:
              "https://media.istockphoto.com/photos/aerial-top-down-shot-of-a-house-frame-on-a-construction-site-by-mud-picture-id1149532577?k=20&m=1149532577&s=612x612&w=0&h=NAPwBwl6rZcMzcGJNaRnKvFeUwVu3Q6cgiyWze7r954=",
            text: "Confiance",
          },
        ],
        style: {
          position: "absolute",
          top: "65%",
          left: "50%",
          transform: "translateX(-50%) translateY(-50%)",
          fontSize: "3vw",
          fontWeight: "bold",
          color: "whitesmoke",
        },
      };




return (
<Context.Provider value={{sliderInfo}}>
{props.children}
</Context.Provider>
)
}

export {ContextProvider, Context}
